    const searchParams = new URLSearchParams(queryString);
    const title = searchParams.get('title');
    const id = searchParams.get('id');
    const getData = async () => {
        try {
            let response = await fetch(`${BASE_URL}/career-job?title=${decodeParams(title)}&id=${id}`);
            let data = await response.json();

            const jobDesk = document.getElementById('job-desk')
            const jobSpec = document.getElementById('job-specification')
            const jobTypes = document.querySelectorAll('#job-type')
            const jobLocations = document.querySelectorAll('#job-location')
            const jobEmail = document.querySelectorAll('#job-email')
            const jobExpDates = document.querySelectorAll('#job-expired-date')
            const jobDeskContent = document.createElement('li')
            const jobSpecContent = document.createElement('li')

            if (data?.id) {
                jobDeskContent.innerHTML = data?.job_desk
                jobSpecContent.innerHTML = data?.qualification
                jobDesk.innerHTML = ''
                jobSpec.innerHTML = ''
                jobDesk.appendChild(jobDeskContent)
                jobSpec.appendChild(jobSpecContent)
                jobTypes.forEach(element => {
                    element.innerHTML = data.career_type.type
                });
                jobLocations.forEach(element => {
                    element.innerHTML = data.career_location.location
                });
                jobExpDates.forEach(element => {
                    element.innerHTML = `Expired Date - ${formatDateInd(data.expired_date)}`
                });
            }

        } catch (error) {
            console.log(error);
        }

    }
    if (title && id) {
        getData()
    }
</script>