<script>
    document.addEventListener('DOMContentLoaded', function() {
        const pathname = window.location.pathname;
        const menuBoxs = document.querySelectorAll('.menu-box')

        menuBoxs.forEach((menu) => {
            if (pathname.includes(menu.id)) {
                menu.classList.add('active')
            } else {
                menu.classList.remove('active')
            }
        })




        const header = document.querySelector('header');
        const scrollThreshold = 50; // Ubah sesuai jumlah pixel scroll yang diinginkan

        const halfScreenHeight = window.innerHeight / 2;

        // Check the Y-position on web first loaded
        if (window.pageYOffset >= scrollThreshold) {
            header.classList.remove('bg-transparent', 'shadow-none');
            header.classList.add('bg-white', 'shadow-lg');
        } else {
            header.classList.add('bg-transparent', 'shadow-none');
            header.classList.remove('bg-white', 'shadow-lg');
        }

        window.addEventListener('scroll', function() {
            if (window.pageYOffset >= scrollThreshold) {
                header.classList.remove('bg-transparent', 'shadow-none');
                header.classList.add('bg-white', 'shadow-lg');
