                icon: `<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 576 512"><path d="M549.7 124.1c-6.3-23.7-24.8-42.3-48.3-48.6C458.8 64 288 64 288 64S117.2 64 74.6 75.5c-23.5 6.3-42 24.9-48.3 48.6-11.4 42.9-11.4 132.3-11.4 132.3s0 89.4 11.4 132.3c6.3 23.7 24.8 41.5 48.3 47.8C117.2 448 288 448 288 448s170.8 0 213.4-11.5c23.5-6.3 42-24.2 48.3-47.8 11.4-42.9 11.4-132.3 11.4-132.3s0-89.4-11.4-132.3zm-317.5 213.5V175.2l142.7 81.2-142.7 81.2z"/></svg>`
            },
            {
                name: "Telegram",
                icon: `<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 496 512"><path d="M248 8C111 8 0 119 0 256S111 504 248 504 496 393 496 256 385 8 248 8zM363 176.7c-3.7 39.2-19.9 134.4-28.1 178.3-3.5 18.6-10.3 24.8-16.9 25.4-14.4 1.3-25.3-9.5-39.3-18.7-21.8-14.3-34.2-23.2-55.3-37.2-24.5-16.1-8.6-25 5.3-39.5 3.7-3.8 67.1-61.5 68.3-66.7 .2-.7 .3-3.1-1.2-4.4s-3.6-.8-5.1-.5q-3.3 .7-104.6 69.1-14.8 10.2-26.9 9.9c-8.9-.2-25.9-5-38.6-9.1-15.5-5-27.9-7.7-26.8-16.3q.8-6.7 18.5-13.7 108.4-47.2 144.6-62.3c68.9-28.6 83.2-33.6 92.5-33.8 2.1 0 6.6 .5 9.6 2.9a10.5 10.5 0 0 1 3.5 6.7A43.8 43.8 0 0 1 363 176.7z"/></svg>`
            },
            {
                name: "Tiktok",
                icon: `<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512" ><!--!Font Awesome Free 6.6.0 by @fontawesome - https://fontawesome.com License - https://fontawesome.com/license/free Copyright 2024 Fonticons, Inc.--><path d="M448 209.9a210.1 210.1 0 0 1 -122.8-39.3V349.4A162.6 162.6 0 1 1 185 188.3V278.2a74.6 74.6 0 1 0 52.2 71.2V0l88 0a121.2 121.2 0 0 0 1.9 22.2h0A122.2 122.2 0 0 0 381 102.4a121.4 121.4 0 0 0 67 20.1z"/></svg>`
            }
        ]

        const selectSocmedItem = (name) => {
            return socmedIcons.find(e => e.name === name)?.icon ?? socmedIcons[0].icon
        }


        const getData = async () => {
            const BASE_URL = document.getElementById('body-contain').getAttribute('data-base-url')

            try {
                let response = await fetch(`${BASE_URL}/footer`)
                const data = await response.json();

                if (data?.data?.length > 0) {
                    const dataFooter = data.data[0]

                    const email = dataFooter.email
                    const phone_number = dataFooter.phone_number
                    const social_media = dataFooter?.social_media ?? []

                    document.getElementById('phone-number').innerHTML = `<svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M13.3781 11.1362C12.4115 10.3095 11.4306 9.80877 10.4759 10.6342L9.9058 11.1331C9.4887 11.4953 8.7132 13.1874 5.71484 9.73821C2.7171 6.2934 4.50101 5.75705 4.91873 5.39802L5.49193 4.8985C6.44164 4.07117 6.08323 3.02967 5.39827 1.95758L4.98491 1.30821C4.29683 0.238611 3.54755 -0.463837 2.59534 0.362242L2.08084 0.811809C1.65999 1.11839 0.483625 2.11493 0.198275 4.00811C-0.145144 6.27967 0.938188 8.88091 3.42017 11.735C5.89903 14.5904 8.32607 16.0247 10.6251 15.9997C12.5358 15.9791 13.6897 14.9538 14.0506 14.581L14.5669 14.1309C15.5166 13.3054 14.9266 12.465 13.9594 11.6364L13.3781 11.1362Z" fill="white" />
                    </svg><span class="ml-2">${phone_number}</span>`
                    document.getElementById('email').innerHTML = `<svg width="21" height="15" viewBox="0 0 21 15" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M20.8151 1.05469L14.4463 7.38252L20.8151 13.7103C20.9303 13.4697 21.0001 13.2037 21.0001 12.9196V1.84543C21.0001 1.56131 20.9303 1.29533 20.8151 1.05469Z" fill="white" />
                        <path d="M19.154 0H1.84543C1.56131 0 1.29533 0.0698495 1.05469 0.18498L9.19475 8.28402C9.91449 9.00376 11.0849 9.00376 11.8046 8.28402L19.9447 0.18498C19.7041 0.0698495 19.4381 0 19.154 0Z" fill="white" />
                        <path d="M0.18498 1.05469C0.0698494 1.29533 0 1.56131 0 1.84543V12.9196C0 13.2037 0.0698494 13.4698 0.18498 13.7103L6.55383 7.38252L0.18498 1.05469Z" fill="white" />
                        <path d="M13.5759 8.25195L12.6746 9.15319C11.4754 10.3524 9.52398 10.3524 8.32473 9.15319L7.42353 8.25195L1.05469 14.5798C1.29533 14.6949 1.56131 14.7648 1.84543 14.7648H19.154C19.4381 14.7648 19.7041 14.6949 19.9447 14.5798L13.5759 8.25195Z" fill="white" />
                    </svg><span class="ml-2">${email}</span>`
                    const socialMediaContainer = document.getElementById('social-media-container')
                    socialMediaContainer.innerHTML = ''

                    if (social_media?.length > 0) {
                        social_media.forEach(element => {
                            const box = document.createElement('a')
                            box.classList.add('sc', 'mx-2')
                            box.href = element.link
                            box.setAttribute('target', "_blank")
                            box.setAttribute('aria-label', element.social_media_name)
                            box.innerHTML = selectSocmedItem(element.social_media_name)
                            socialMediaContainer.appendChild(box)
                        });
                    }

                    const liveChats = document.querySelectorAll('#live-chat')

                    liveChats.forEach((menu) => {
                        menu.href = `https://wa.me/${dataFooter.phone_number}`
                    })
                }

            } catch (error) {
                console.log(error);
            }
        }
        getData()

    });
</script>

</body>

</html>